import { Component, ViewEncapsulation } from '@angular/core';
import SwiperCore, { Autoplay, Navigation, EffectCards, EffectCoverflow} from "swiper";

SwiperCore.use([Autoplay, Navigation, EffectCards, EffectCoverflow]);


@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss'],
  encapsulation: ViewEncapsulation.None,

})
export class MenuComponent {

}
<swiper
  [effect]="'coverflow'"
  [grabCursor]="true"
  [centeredSlides]="true"
  [slidesPerView]="'auto'"
  [coverflowEffect]="{
    rotate: 50,
    stretch: 0,
    depth: 100,
    modifier: 1,
    slideShadows: true
  }"
  [pagination]="true"
  class="mySwiper"
>
  <ng-template swiperSlide
    ><img src="../../../assets/menu/menu1.png" /> </ng-template
  ><ng-template swiperSlide
    ><img src="../../../assets/menu/menu2.png" /> </ng-template
  ><ng-template swiperSlide
    ><img src="../../../assets/menu/menu3.png" /> </ng-template>
</swiper>
